void runLEDs()
{
  for (;;)
  {
    if (taserActivated) {
      Serial0.println("Activating red lights for taser");
      FastLED.setBrightness(10);
      fill_solid(leds, 120, CRGB::Red);
      FastLED.show();
    }
    else if (pianoLoading)
    {
      FastLED.setBrightness(10);
      fill_solid(leds, 120, CRGB::White);
      FastLED.show();
    }
    else if (!gameOn)
    {
      FastLED.setBrightness(10);
      uint8_t thisHue = beat8(10, 255);
      fill_rainbow(leds, 120, thisHue, 10);
      FastLED.show();
    } else
    {
      FastLED.setBrightness(10);
      displayNotes();
    }
  }
}

